mapper.select:
SELECT board.*, category.name AS category_name FROM board JOIN category ON board.category_id = category.id
AND registration_date BETWEEN CONCAT(#{startDate}, ' ', #{START_TIME}) AND CONCAT(#{endDate}, ' ', #{END_TIME})
AND category_id = #{category}
AND (title LIKE CONCAT('%', #{searchText}, '%') OR writer LIKE CONCAT('%', #{searchText}, '%') OR content LIKE CONCAT('%', #{searchText}, '%'))
ORDER BY registration_date DESC LIMIT #{startNum}, #{limit}

mapper.update:
UPDATE board
SET view_count = view_count + 1
WHERE board.id = #{id}

mapper.select:
SELECT COUNT(*)
FROM board
WHERE id = #{id} AND password = #{password}
