mapper.select:
SELECT count(*) FROM board WHERE registration_date BETWEEN #{startDate} AND #{endDate}
AND category_id = #{category}
AND (title LIKE CONCAT('%', #{searchText}, '%') OR writer LIKE CONCAT('%', #{searchText}, '%') OR content LIKE CONCAT('%', #{searchText}, '%'))

SELECT board.*, category.name AS category_name
FROM board
JOIN category ON board.category_id = category.id
WHERE registration_date BETWEEN #{startDate} AND #{endDate}

    AND category_id = #{category}


    AND (title LIKE CONCAT('%', #{searchText}, '%')
        OR writer LIKE CONCAT('%', #{searchText}, '%')
        OR content LIKE CONCAT('%', #{searchText}, '%'))

ORDER BY registration_date DESC
LIMIT #{startNum}, #{limit}
